mapper.select:
select
group_concat(tc.sellcarcount) as sellcarcount, tc.carvolume as carvolume, tc.visitorsum as visitorsum, tc.purchasesum as purchasesum, (select count(tc.carstatus) from t_car tc) as cangkucount, (select count(tc.visitcarstatu) from t_car tc where tc.visitcarstatu = 1) as visitcount, (select count(tc.expocarstatu) from t_car tc where tc.expocarstatu = 1) as expocarcount, (select count(tc.placecarstatu) from t_car tc where tc.placecarstatu = 1) as placecarcount, (select count(tc.mentcarstatu) from t_car tc where tc.mentcarstatu = 1) as mentcarcount
from t_car tc where DATE_SUB(CURDATE(), INTERVAL 7 DAY) < date(tc.cardate)
