mapper.select:
select * from car_type a,t_user s,details d,orders o where a.id=o.carid and o.detaid=d.oid and s.userid=o.userid

    and o.xdtime >=#{p.qian}


    and o.xdtime <=#{p.ho}

     limit #{page},#{pageSize}

 select * from refund r,car_type t where  r.carid=t.id


 and r.dtime >=#{s.qian}


 and r.dtime <=#{s.ho}



 and r.fangshi=#{s.fang}



 and r.status=#{s.zhuang}


and r.zhuangtai=#{s.kuan}


and r.reason=#{s.re}

 limit #{page},#{pageSize}

   select * from t_user u,comment c,car_type t where c.userid=u.userid  and c.carid=t.id


       and c.ptime >=#{a.qian}


       and c.ptime <=#{a.ho}


       and c.status=#{a.kuan}

limit #{page},#{pageSize}
